import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './Signup.css';
import Home from "../Home/Home";
import BasicTextFields from "../../Components/textfield";
const Signup=()=>{
    const navigate=new useNavigate();
    // const [name,setName]=useState("");
    // const [password,setPassword]=useState("");

    const [formData,setformData]=useState(
        {
            name:"",
            password:"",
            email:"",
            gender:""
        }
    )
    const handleChange=(e)=>{
        const {name,value}=e.target;
        setformData({
            ...formData,
            [name]:value
        })
    }
    // const nameChangeHandler=(e)=>{
    //     setName(e.target.value);
    // }
    // const passwordChangeHandler=(e)=>{
    //     setPassword(e.target.value);
    // }
    const submitHandler=()=>{
       // alert(password);
       navigate("/home",{state:{name:formData.name,password:formData.password,email:formData.email,gender:formData.gender}})
    }
    return(
    <div>           
        <div className="container">  
            {false&&<h2>hhuhu</h2>}
            <h1 className="top-items">Sign Up</h1>
            <h2 className="top-items">Authenticator</h2>
            <form className=".form" onSubmit={submitHandler}>
                <BasicTextFields className=".input" placeHolder="Enter Email"  onChange={handleChange} value={formData.email} name="email"/>
                <BasicTextFields className=".input" placeHolder="Enter Name" onChange={handleChange} value={formData.name} name="name" type="text"/>
                <BasicTextFields className=".input" placeHolder="Enter Password" onChange={handleChange} value={formData.password} name="password" type="password"/>
                <select className=".input border=" id="gender" name="gender" value={formData.gender} onChange={handleChange}>
                    <option value="Gender">Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                </select>
                <button type="submit">Submit</button>
           </form>
        </div>
        {/* <Home name={formData.name} email={formData.email} password={formData.password} gender={formData.gender}>
        </Home> */}
    </div>)
}

export default Signup
